using System;using System.Collections.Generic;using System.Threading.Tasks;using Microsoft.EntityFrameworkCore;using NetNote.IService;using NetNote.Models;namespace NetNote.Service{  public class NoteTypeRepository : INoteTypeRepository  {    private NoteMysqlContext context;    public NoteTypeRepository(NoteMysqlContext _context)    {      this.context = _context;    }    public Task<List<NoteType>> GetListAsync()    {      return context.NoteTypes.ToListAsync();    }  }}